import React, { Component } from "react";
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, CardText, Button, CardMenu, IconButton } from "react-mdl";
class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = { activeTab: 0 };
  }

  toggleCategories() {
    if (this.state.activeTab === 0) {
      return (
       <Card shadow={5} style={{minWidth: '450', margin: 'auto'}}>
       <CardTitle style={{color: '#fff', height: '176px', background:'url(https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVem8GN93kSHXYVpR_zBdYv5CMBrP_kvlB2vyKsheVP1Bpot4SuA) center / cover'}} >React Project #1</CardTitle>
       <CardText>
           Lorem ipums shf sfshf sfs afsd g fgd d dfh dh dfh gf jf jfg jfh j
       </CardText>
       <CardActions border>
       <Button colored>GitHub</Button>
       <Button colored>Heroku</Button>
       <Button colored>Live-Demo</Button>
       </CardActions>
       </Card>
        //   <h1>Projects I built with React</h1>

      );
    } else if (this.state.activeTab === 1) {
      return (
        <div>
          <h1>Projects I built using Javascript and jQuery</h1>
        </div>
      );
    } else if (this.state.activeTab === 2) {
      return (
        <div>
          <h1>Projects I built using Javascript to communicate with API's</h1>
        </div>
      );
    } else if (this.state.activeTab === 3) {
      return (
        <div>
          <h1>Projects I built using Bootstrap</h1>
        </div>
      );
    }
  }

  render() {
    return (
      <div className="category-tabs">
        <Tabs
          activeTab={this.state.activeTab}
          onChange={tabId => this.setState({ activeTab: tabId })}
          ripple
        >
          <Tab>React</Tab>
          <Tab>Javascript/jQuery</Tab>
          <Tab>JS with API's</Tab>
          <Tab>Bootstrap</Tab>
        </Tabs>
        <section className="projects-grid">
          <Grid className="projects-grid">
            <Cell col={12}>
            <div className="content">{this.toggleCategories()}</div>
            </Cell>
          </Grid>
          {/* {this.toggleCategories()} */}
        </section>
      </div>
    );
  }
}

export default Projects;
